bool board::win(){
	for(int i=0;i<m;i++){
		for(int j=0;j<n;j++){
			if(bomb[i][j]!=9&&(symbol[i][j]==-2||symbol[i][j]==-79)) return false;
		}
	}
	return true;
}
void board::reset_board(){
	for(int i=0;i<m;i++){
		for(int j=0;j<n;j++) bomb[i][j]=0;
	}
}
